mapper.select:
SELECT * FROM ( SELECT rownum rnum, B.* FROM ( SELECT board_no, region,region_detail,title, content, writer_id, writer_nick, hit, writendate FROM tripspot
region LIKE '%'||#{name}||'%' AND region_detail LIKE '%'||#{search}||'%'
ORDER BY board_no DESC ) B ORDER BY rnum )WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE tripspot
SET hit = hit+1
WHERE board_no = #{board_no}
